package mypackage;
import java.io.*;
public class EvenOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int num=21;
        if(num%2==0) {
        	System.out.println("Entered Number is Even");
        }
        else {
        	System.out.println("Entered Number is Odd");
        }
	}

}
